mapper.insert:
insert into mg_note_info (note_value, user_mobile,
  send_status, res_contents, insert_time
  )
values (#{noteValue,jdbcType=VARCHAR}, #{userMobile,jdbcType=VARCHAR},
  #{sendStatus,jdbcType=VARCHAR}, #{resContents,jdbcType=VARCHAR}, #{insertTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select note_id, note_value, user_mobile, send_status, res_contents, insert_time
from mg_note_info
where note_id = #{noteId,jdbcType=INTEGER}
